import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import SingleProduct from './SingleProduct';


import img1 from '../assets/img/featured/1.jpg';
import img2 from '../assets/img/featured/2.jpg';
import img3 from '../assets/img/featured/3.jpg';
import img4 from '../assets/img/featured/4.jpg';
import img5 from '../assets/img/featured/5.jpg';
import img6 from '../assets/img/featured/6.jpg';
import img7 from '../assets/img/featured/7.jpg';
import img8 from '../assets/img/featured/8.jpg';
import img9 from '../assets/img/featured/9.jpg';
import img10 from '../assets/img/featured/10.jpg';

const products=[
    {price1:'99.999,',name1:'The historian',price2:'99.999',name2:'Done With that',img1:img1, img2:img2},
    {price:'99.999',name:'Prople of the book',price2:'91.999',name2:'How to leave',img1:img3, img2:img4},
    {price:'99.999',name:'The secret',price2:'99.999',name2:'Whatever u have',img1:img5, img2:img6},
    {price:'99.999',name:'The hero',price2:'99.999',name2:'Buck that Fitch',img1:img7, img2:img8},
    {price:'99.999',name:'The hero',price2:'99.999',name2:'Done With that',img1:img9, img2:img10},
];

class Products extends Component {
    render(){
        return(
            <div className="featured-product-area section-padding">
            <h2 className="section-title">FEATURED PRODUCTS</h2>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <div className="tab-menu">
                            <ul className="nav nav-tabs" role="tablist">
                                <li role="presentation" className="first-item active">
                                    <Link to="/arrival" aria-controls="arrival" role="tab" data-toggle="tab">New Arrival</Link>
                                </li>
                                <li role="presentation">
                                    <Link to="/sale" aria-controls="sale" role="tab" data-toggle="tab">BEST SELLERS</Link>
                                </li>
                            </ul>
                        </div>         
                    </div>
                </div>   
                <div className="row">
                    <div className="product-list tab-content">
                        <div role="tabpanel" className="tab-pane fade in active" id="arrival">
                            <div className="featured-product-list indicator-style owl-carousel owl-theme">
                                <div className="owl-wrapper-outer">
                                    <div className="owl-wrapper">
                                        {products.map((product, i) => {
                                            return <SingleProduct {...product} key={i} />
                                        })}
                                    </div>
                                </div>                                            
                            </div>
                        </div>
                    </div>
                </div>             
            </div>
		</div>
        )
    }
}
export default Products;
