import React, {Component} from 'react';
import {Link} from 'react-router-dom';

import '../assets/css/header.css';
import '../assets/css/page.css';

import BlockContent from './DanhmucSP/BlockContent';
import SlideShow from './SlideShow';

import img1 from '../assets/img/slider/1.jpg';
import img2 from '../assets/img/slider/2.jpg';
import img3 from '../assets/img/slider/2.jpg';
import img4 from '../assets/img/slider/2.jpg';
import img5 from '../assets/img/slider/2.jpg';

const contents = [
    {name: "Sach trong", title: 'sach trong nuoc', link: ''},
    {name: "Sach trong nuoc", title: 'sach trong nuoc', link: ''},
    {name: "Sach trong nuoc", title: 'sach trong nuoc', link: ''},
    {name: "Sach trong nuoc", title: 'sach trong nuoc', link: ''},
    {name: "Sach trong nuoc", title: 'sach trong nuoc', link: ''},
    {name: "Sach trong nuoc", title: 'sach trong nuoc', link: ''},
    {name: "Sach trong nuoc", title: 'sach trong nuoc', link: ''},
    {name: "Sach trong nuoc", title: 'sach trong nuoc', link: ''},
    {name: "asfoewodfco", title: 'sach trong nuoc', link: ''}
];

const collections = [
    {src: img1,caption: ""},
    {src: img2,caption: ""},
    {src: img3,caption: ""},
    {src: img4,caption: ""},
    {src: img5,caption: ""}
];

class Header extends Component {
    render(){
        return(
            <section id="header" className="header">
                <div className="header-breadcrumbs background-menu-homepage">
                    <div className="container">
                        <div className="row custom-menu-homepage">
                            <div className="col-md-3 hidden-max-width-992">
                                <div id="ves-verticalmenu" className="block block-success ves-verticalmenu highlighted hidden-sm offcanvas-actived">
                                    <div className="block-title hidden-xs hidden-sm">
                                        <span>Danh muc san pham</span>
                                        <em className="shapes right"></em>
                                        <em className="line"></em>
                                    </div>
                                    {contents.map((content, index) => {
                                        return <BlockContent {...content} key={index} />
                                    })}
                                </div>
                            </div>
                            <div className="col-md-9">
                                <div className="container">
                                    <div className="container-inner">
                                        <div className="row">
                                            <div className="col-sms12 col-md-9 col-sm-12">
                                                <div className="inivoslider swiper-container swiper-container-horizontal">
                                                    <div className="slider-header">
                                                        <SlideShow 
                                                            input={collections}
                                                            ratio={`3:2`}
                                                            mode={`automatic`}
                                                            timeout={`3000`}
                                                        />
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            
        );
    }
}



export default Header;
