import React, {Component} from 'react';
import '../assets/css/home.css';

import img1 from '../assets/img/blog/1.jpg';
import img2 from '../assets/img/blog/1.jpg';
import img3 from '../assets/img/blog/1.jpg';
import SingleBlog from './SingleBlog';

const blogs = [
    {img:img1,title:'Modern Book Reviews', source:'LASTEST BLOG',date:'20-2-2020'},
    {img:img2,title:'Modern Book Reviews', source:'LASTEST BLOG',date:'20-2-2020'},
    {img:img3,title:'Modern Book Reviews', source:'LASTEST BLOG',date:'20-2-2020'}
];

class BlogArea extends Component {
    render(){
        return(
            <div className="blog-area section-padding">
                <h2 className="section-title">LATEST BLOG</h2>
                <p>The Latest Blog post for the biggest Blog for the books Library.</p>
		        <div className="container">
                    <div className="row">
                        <div className="blog-list indicator-style owl-carousel owl-theme">
                            <div className="owl-wrapper-outer">
                                <div className="owl-wrapper blog-area-wrapper">
                                    {blogs.map((blog,i) => {
                                        return <SingleBlog {...blog} key={i} />
                                    })}                
                                </div>
                            </div>
                            <div className="owl-controls clickable">
                                <div className="owl-buttons">
                                    <div className="owl-prev">
                                        <i className="fa fa-angle-left"></i>
                                    </div>
                                    <div className="owl-next">
                                        <i className="fa fa-angle-right"></i>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default BlogArea;
