import React,{Component} from 'react';
import Header from '../Common/Header/Header';
import {connect} from 'react-redux';
import {withRouter, Link, Link as RouterLink} from 'react-router-dom';
import * as SiteActions from '../../store/actions/siteActions';
import ShopItems from '../../components/Common/ShopItems';

import '../assets/css/blog.css';
import '../assets/css/shop.css';


import clsx from 'clsx';
import {withStyles} from '@material-ui/core/styles';


import image from '../assets/img/shop-info-bg.jpg';

import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import IconButton from '@material-ui/core/IconButton';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';

import Sidebar from '../Common/SideBar';



const drawerWidth= 240;

const styles = theme => ({
    storeButton: {
        width: '250px',
        margin: '10px auto',
        display: 'block',
        fontSize: '14px',
        marginBottom: '10px',
        color: '#fff',
        fontWeight: 'bold',
        textAlign: 'center',
        padding: '10px',
        backgroundColor: '#ff9800',
        borderRadius: '25px'
    },
    buttonSize: {
        fontSize: 'large',
        padding: '0',
        width: '100%'
    },
    drawerPaper: {
        position: 'relative',
        whiteSpace: 'noWrap',
        width: drawerWidth,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen
        }),
    },
    drawerPaperClose: {
        overflowX: 'hidden',
        width: theme.spacing(7),
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen
        }),
    },
    toolbarIcon: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: '0 8px',
        ...theme.mixins.toolbar
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
        height: '100vh',
        overflow: 'auto'
    },
    list: {
        width: 250,
    },
        fullList: {
        width: 'auto',
    },
    list: {
        width: 250,
    },
    fullList: {
        width: 'auto',
    },
});

class Shop extends Component {
    constructor(props){
        super(props);

        this.state = {
            open: false
        }
    }

    toggleDrawer = (e) => {
        if (e.type === 'keydown' && (e.key === 'Tab' || e.key === 'Shift')) {
            return;
        }
        this.setState({open: true})
        
    };


    componentDidMount(){
        this.props.getProducts(0);
        this.props.getProductCount();
    }
    render(){
        const {classes} = this.props;

        return(
            <div>
                <div className="page-empty-banner col-lg-12 col-md-12 col-sm-12">
                    <div className="page-banner page-banner-tm">
                        <Link to="">
                            <img src={image} alt="" />
                        </Link>
                    </div>
                    <div className="col-main">
                        <div className="container">
                            <div className={classes.storeButton}>
                                <Button className={classes.buttonSize} onClick={this.toggleDrawer(true)}>Cửa hàng</Button>
                                <Drawer anchor="left" open={this.state.open} onClose={this.toggleDrawer(false)}>
                                    <div className={classes.list} role= "presentation" onClick={this.toggleDrawer(false)} onKeyDown={this.toggleDrawer(false)}>
                                        <List>
                                            <ListItem button >
                                                <ListItemIcon>

                                                </ListItemIcon>
                                                <ListItemText />
                                            </ListItem>
                                        </List>
                                        <Divider />
                                        <List>

                                        </List>
                                    </div>
                                </Drawer>
                            </div>
                        </div>
                    </div>
                </div>
                <div>
                    <section className="bg-light" id="portfolio">
                        <div className="container">
                            
                            <div className="row">
                                
                                {this.props.site.products ?
                                    this.props.site.products.length > 0 ?
                                        this.props.site.products.map((product, i) => {
                                            return (
                                                <ShopItems
                                                    product={product}
                                                    key={i} 
                                                />
                                            )
                                        })
                                    :null
                                :null}
                            </div>
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="text-center">
                                        {this.props.site.products ?
                                            this.props.site.productCount > this.props.site.products.length ?
                                                <button className="btn btn-default" onClick={e => {
                                                    this.props.getProducts(this.props.site.products.length);
                                                }}>Xem thêm...</button>
                                            :null
                                        :null}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                </div>
                
            </div>
        )
    }
}
const mapStateToProps = state => ({
    site: state.site
})

const mapDispatchToProps = dispatch => ({
    getProducts: (skip) => {
        dispatch(SiteActions.getProducts(skip));
    },
    getProductCount: () => {
        dispatch(SiteActions.getProductCount());
    }
})

export default withRouter(connect(
    mapStateToProps,
    mapDispatchToProps
)(withStyles(styles)(Shop)));
