import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import API from '../../utils/api';
import Badge from '@material-ui/core/Badge';
import ShoppingCart from '@material-ui/icons/ShoppingCart';
import * as SiteActions from '../../store/actions/siteActions';

import '../assets/css/shop.css';


import CartItems from './CartItems';


class Cart extends Component {
    render(){
        let addedProducts = this.props.products.inventory> 0 ?
        (
            this.props.products.map((product, i) => {
                return(
                    <CartItems 
                        product={product}
                        key={i}
                    />
                )
            })
        ):
        (
            <h1 style={{color: '#f1e2d3', marginLeft: '50px', paddingLeft: '50px'}}>Khong co' gi` @@</h1>    
        )
        return(
            <div className="float-cart_content_tm">
                <div className="float-cart_header_tm">
                    <span>
                    <Badge className="bag_tm" badgeContent={addedProducts.length} color="secondary" >
                        <ShoppingCart style={{fontSize: '40px'}} onClick={this.toggleDrawer} color ="primary" /> 
                    </Badge>
                    </span>
                    <span className="header-title_tm">Giỏ Hàng</span>
                </div>
                <div className="float-cart__shelf-container_tm">
                    {addedProducts}
                </div>
                <div className="float-cart_footer_tm">
                    <div className="sub_tm">Tổng số tiền: </div>
                    <div className="sub-price_tm">
                        <p className="sub-price_val_tm">{this.props.site.total}</p>
                        <small className="sub-price_installment_tm">
                            <span></span>
                        </small>
                    </div>
                    <div className="buy-btn_tm">Mua ngay</div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
        site: state.site,
        products: state.site.addedProducts
})

const mapDispatchToProps = dispatch => ({
    
})

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Cart);
