import React, {Component} from 'react';
import {withFormik} from 'formik';
import * as Yup from 'yup';
import Field from '../Common/Field';
import {connect} from 'react-redux';
import * as AuthActions from '../../store/actions/authActions';

const fields= [
    {name: 'email',type: 'email', placeholder: 'Email address'},
    {name: 'password',type: 'password', placeholder: 'Password'}
];

class Login extends Component {
    constructor(props){
        super(props);

        this.state = {
            email: '',
            password: ''        
        }
    }
    render(){
        return(
        <div className="login-account section-padding">
           <div className="container">
                <div className="row">
                    
                    <div className="col-md-6 col-sm-6">
                       <form onSubmit={this.props.handleSubmit} className="create-account-form" method="post">
                            <h2 className="heading-title">
                                LOGIN
                            </h2>
                            {fields.map((f, i) => {
                                return <Field   
                                            {...f}
                                            key={i} 
                                            value={this.props.values[f.name]}
                                            name={f.name}
                                            onChange={this.props.handleChange}
                                            onBlur={this.props.handleBlur}
                                                
                                        />
                            })}
                            <p className="lost-password form-group">
                                <a href="#" rel="nofollow">Forgot your password?</a>
                            </p> 
                            <div className="submit">					
                                <button name="submitcreate" id="submitcreate" type="submit" className="btn-default">
                                    <span>
                                        <i className="fa fa-user left"></i>
                                        SIGN IN
                                    </span>
                                </button>
                            </div>                          
                       </form>
                    </div>
                    <div className="col-md-6 col-sm-6">
                        <form action="#" className="create-account-form" method="post">
                            <h2 className="heading-title">
                                CREATE AN ACCOUNT
                            </h2>
                            <div className="submit">					
                                <button name="submitcreate" id="submitcreate" type="submit" className="btn-default">
                                    <span>
                                        <i className="fa fa-user left"></i>
                                        Create an account
                                    </span>
                                </button>
                            </div>
                        </form>
                    </div>
                </div>               
           </div>
        </div>
        )
    }
}



const mapStateToProps = state => {
    return {
        auth: state.auth
    }
}

const mapDispatchToProps = dispatch => {
    return {
        login: (email, pass) => {
            dispatch(AuthActions.login(email, pass));
        }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(withFormik({
    mapPropstoValues: () => ({
        email: '',
        password: '',
    }),
    validationSchema: Yup.object().shape({
        email: Yup.string().email('Email is invalid.').required('Login with email.'),
        password: Yup.string().required('Enter Password')
     }),

    handleSubmit: (values, {setSubmitting}, login) => {
        console.log("Login attempt", values);
        login(values.email, values.password);
    }
})(Login));
