import React, {Component} from 'react';
import SingleInfo from './SingleInfo';

const infos=[
    {title:'Vận chuyển miễn phí', caption:'Trong bán kính 1km quanh cơ sở', icon:'transport', buttonText:'xem thêm'},
    {title:'Liên lạc trực tiếp', caption:'', icon:'social', buttonText:'chat ngay'},
    {title:'Đặt trước', caption:'quý khách order trước hàng hóa hiện chưa có trong nhà sách', icon:'school', buttonText:'Order now'},
];

class Info extends Component{
    render(){
        return(
            <div className="shop-info-area">
                <div className="container">
                    <div className="row">
                        {infos.map((info, i) => {
                            return <SingleInfo {...info} key={i} />
                        })}
                        
                    </div>
                </div>
            </div> 
        );
    }
}

export default Info;
