import React,{Component} from 'react';
import Header from '../Common/Header/Header';
import {connect} from 'react-redux';
import {withRouter, Link, Link as RouterLink} from 'react-router-dom';
import * as SiteActions from '../../store/actions/siteActions';
import ShopItems from '../../components/Common/ShopItems';

import '../assets/css/blog.css';
import '../assets/css/shop.css';


import clsx from 'clsx';
import {withStyles} from '@material-ui/core/styles';


import image from '../assets/img/shop-info-bg.jpg';

import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import IconButton from '@material-ui/core/IconButton';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';

import Sidebar from '../Common/SideBar';
import HomeShop from '../Common/Body/HomeShop';
import BlogItems from '../Common/BlogItems';



const drawerWidth= 240;

const styles = theme => ({
    storeButton: {
        width: '250px',
        margin: '10px auto',
        display: 'block',
        fontSize: '14px',
        marginBottom: '10px',
        color: '#fff',
        fontWeight: 'bold',
        textAlign: 'center',
        padding: '10px',
        backgroundColor: '#ff9800',
        borderRadius: '25px'
    },
    buttonSize: {
        fontSize: 'large',
        padding: '0',
        width: '100%'
    },
    list: {
        width: 250,
    },
    fullList: {
        width: 'auto',
    },
    list: {
        width: 250,
    },
    fullList: {
        width: 'auto',
    },
});


class Shop extends Component {
    constructor(props){
        super(props);

        this.state = {
            open: false
        }
    }

    toggleDrawer = (event) => {
        if(this.state.open === true){
            this.setState({open: false})
        }else{
            this.setState({open: true})
        }
        
    };



    render(){
        const {classes} = this.props;

        return(
            <div>
                <div className="page-empty-banner col-lg-12 col-md-12 col-sm-12">
                    <div className="page-banner page-banner-tm">
                        <Link to="">
                            <img src={image} alt="" />
                        </Link>
                    </div>
                    <div className="col-main">
                        <div className="container">
                            <div className={classes.storeButton}>
                                <Button className={classes.buttonSize} onClick={this.toggleDrawer}>Cửa hàng</Button>
                                
                            </div>
                        </div>
                    </div>
                </div>
                <div className="container">
                <div className="row">
                        {this.props.site.posts ?
                            this.props.site.posts.length > 0 ?
                                this.props.site.posts.map((post, i) => {
                                    return (
                                        <BlogItems
                                            post={post}
                                            key={i} 
                                        />
                                    )
                                })
                            :null
                        :null}
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-12">
                        <div className="text-center">
                            {this.props.site.postCount > this.props.site.posts.length ?
                                <button className="btn btn-default" onClick={e => {
                                    this.props.getPosts(this.props.site.posts.length);
                                }}>Xem them...</button>
                            :null}
                        </div>
                    </div>
                </div>
                <Drawer anchor="left" open={this.state.open} onClose={this.toggleDrawer}>
                    <div className={classes.list} role= "presentation" onClick={this.toggleDrawer} onKeyDown={this.toggleDrawer}>
                        <List>
                            <Link to="/">
                            <ListItem button >
                                <ListItemIcon>
                                    
                                    <InboxIcon/>
                                    
                                </ListItemIcon>
                                <ListItemText />
                            </ListItem>
                            </Link>
                            <ListItem button >
                                <ListItemIcon>
                                    <MailIcon />
                                </ListItemIcon>
                                <ListItemText />
                            </ListItem>
                        </List>
                        <Divider />
                        <List>
                            <ListItem button >
                                <ListItemIcon>
                                    <InboxIcon />
                                </ListItemIcon>
                                <ListItemText />
                            </ListItem>
                            <ListItem button >
                                <ListItemIcon>
                                    <MailIcon />
                                </ListItemIcon>
                                <ListItemText />
                            </ListItem>
                        </List>
                    </div>
                </Drawer>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    site: state.site
})

const mapDispatchToProps = dispatch => ({
    
})

export default withRouter(connect(
    mapStateToProps,
    mapDispatchToProps
)(withStyles(styles)(Shop)));
