import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import API from '../../utils/api';
import Badge from '@material-ui/core/Badge';
import ShoppingCart from '@material-ui/icons/ShoppingCart';
import * as SiteActions from '../../store/actions/siteActions';

import Drawer from '@material-ui/core/Drawer';
import Divider from '@material-ui/core/Divider';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';

import classNames from 'clsx';
import {fade, withStyles} from '@material-ui/core/styles';

import '../assets/css/shop.css';


import CartItems from './CartItems';


const styles = theme => ({
    root: {
        display: 'block',
    },
    sectionDesktop: {
        display: 'none',
        [theme.breakpoints.up('md')]: {
          display: 'block',
        },
        padding: '4px',
        fontSize: '1.1em',
        borderBottom: '1px solid #ced4da',
        
    },
    sectionMobile: {
        display: 'block',
        [theme.breakpoints.up('md')]: {
          display: 'none',
        },
        padding: '4px',
        fontSize: '1.1em',
    },
    cartCheck: {
        width: '100%',
    },
    subtotal: {
        paddingBottom: '4px',
        display: '-webkit-flex',
        flexDirection: 'row',
        justifyContent: 'flex-start',
        [theme.breakpoints.down('md')]: {
            flexDirection: 'column',
            width: '100%',
        }
    },
    subtotalText: {
        [theme.breakpoints.up('md')]: {
            width: 'calc(100% - 150px)'
        },
        width: 'calc(100% - 120px)',
        textAlign: 'right',
    },
    subtotalMoney: {
        [theme.breakpoints.up('md')]: {
            width: '150px'
        },
        width: '120px',
        textAlign: 'right',
    },
    grandtotalText: {
        [theme.breakpoints.up('md')]: {
            width: 'calc(100% - 150px)'
        },
        width: 'calc(100% - 120px)',
        textAlign: 'right',
        fontWeight: '600',
    },
    grandtotalMoney: {
        [theme.breakpoints.up('md')]: {
            width: '150px'
        },
        width: '120px',
        textAlign: 'right',
        fontSize: '1.2em',
        fontWeight: '700',
        color: '#f39801'
    },
    back: {
        cursor: 'pointer',
        color: '#555555',
        fontWeight: 600,
        fontSize: '14px',
    }
});

class Cart extends Component {
    constructor(props){
        super(props);

        this.state = {
            open: false,
            anchor: null
        }
    }
    
    toggleDrawer = () => {
        if(this.state.open){
            this.setState({open: false})
        }else{
            this.setState({open: true})
        }
    }
    render(){
        
        const {classes} = this.props;

        return(
            <div className="float-cart_content_tm">
                <div className="float-cart_header_tm">
                    <span>
                    <Badge className="bag_tm" badgeContent={this.props.products.length} color="secondary" >
                        <ShoppingCart style={{fontSize: '40px'}} onClick={this.toggleDrawer} color ="primary" /> 
                    </Badge>
                    </span>
                    <span className="header-title_tm">Giỏ Hàng</span>
                </div>
                <div className="float-cart_shelf-container_tm">
                    {
                            this.props.site.addedProducts.map((product, i) => {
                                return(
                                    <CartItems 
                                        product={product}
                                        key={i}
                                    />
                                )
                            })
                    }    
                </div>
                <div className="float-cart_footer_tm">
                    <div className="sub_tm">Tổng số tiền: </div>
                    <div className="sub-price_tm">
                        <p className="sub-price_val_tm">{this.props.site.total} đ</p>
                        <small className="sub-price_installment_tm">
                            <span></span>
                        </small>
                    </div>
                    <div className="buy-btn_tm" onClick={this.toggleDrawer}>Mua ngay</div>
                </div>
                <Drawer
                    classes={{
                        paper: classes.cartCheck
                    }}
                    anchor="bottom"
                    open={this.state.open}
                    onClose={this.toggleDrawer}
                >
                    <div className="container">
                        <div className={classes.sectionDesktop}>
                            <div className={classes.subtotal}>
                                <div className={classes.subtotalText}>Thành tiền</div>
                                <div className={classes.subtotalMoney}>{this.props.site.total}</div>
                            </div>
                            <div className={classes.subtotal}>
                                <div className={classes.grandtotalText}>Tổng số tiền</div>
                                <div className={classes.grandtotalMoney}>{this.props.site.total} </div>
                            </div>
                        </div>
                        <div className={classes.sectionMobile}>
                            <div className={classes.subtotal}>
                                <div className={classes.grandtotalText}>Tổng số tiền</div>
                                <div className={classes.grandtotalMoney}>{this.props.site.total} </div>
                            </div>
                        </div>
                        <Divider />
                        <div>
                            <Link className={classes.back} onClick={this.toggleDrawer}>
                                <span style={{paddingRight: '8px'}}>
                                    <ArrowBackIcon />
                                </span>
                                <span>Tiếp tục mua sắm</span>
                            </Link>
                        </div>
                    </div>
                </Drawer>
            </div>
        )
    }
}

const mapStateToProps = state => ({
        site: state.site,
        products: state.site.addedProducts
})

const mapDispatchToProps = dispatch => ({
    
})

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(withStyles(styles)(Cart));
