import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import {withStyles} from '@material-ui/core/styles'

const styles = theme => ({
    col: {
        padding: '5px',
        width: '33.3333333333333333333333%',
        float: 'left',
        position: 'relative',
        minHeight: '1px',
    },
    img: {
        height: '214px',
    },
    text: {
        textAlign: 'center',
        padding: '5px',
        fontSize: '18px',
        color: '#ff9800',
        fontWeight: '700',
    },
})

class PeopleAndBooks extends Component {
    render(){
        const {classes} = this.props;
        return(
            <div className={classes.col}>
                <Link to={this.props.link}><img className={classes.img} src={this.props.img} alt=""/></Link>
                <Link to={this.props.link}><div className={classes.text}>{this.props.name}</div></Link>
            </div>
        )
    }
}

export default withStyles(styles)(PeopleAndBooks);
