import React, {Component} from 'react';
import {Link} from 'react-router-dom';

import '../assets/css/home.css';

import image from '../assets/img/slider/1.jpg'

import SlideShow from '../Common/SlideShow';

import Header from '../Common/Header';
import Banner from '../Common/Banner';
import Info from '../Common/Info';
import Products from '../Common/Products';
import Testimonial from '../Common/Testimonial';
import BlogArea from '../Common/BlogArea';
import Footer from '../Common/Footer';

import img1 from '../assets/img/slider/1.jpg';
import img2 from '../assets/img/slider/2.jpg';

const collections = [
    {src: img1,caption: "11111111111"},
    {src: img2,caption: "22222222222"}
];

class Home extends Component {
    render(){
        return(
			<div>
                <SlideShow
                    input={collections}
                    ratio={`3:2`}
                    mode={`manual`}
                />
                <SlideShow
                    input={collections}
                    ratio={`3:2`}
                    mode={`automatic`}
                    timeout={`3000`}
                />
                <Banner />
                <Info />
                <Products />
                <Testimonial />
                <BlogArea />
                <Footer />
            </div>
            
        )
    }
}

export default Home;
