import React, {Component} from 'react';
import API from '../../utils/api';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import * as SiteActions from '../../store/actions/siteActions';

import AddIcon from '@material-ui/icons/Add';

class ShopItems extends Component {
    render(){
        return(
            <div className="item sl-width col-md-4 col-sm-6 portfolio-item">
                <Link 
                    className="portfolio-link" 
                    to={`/shop/${this.props.product.id}`}
                    onClick={e => this.props.setProductData(this.props.product)}
                >
                    <div className="portfolio-hover">
                        <div className="portfolio-hover-content">
                            <p><AddIcon style={{fontSize: '5em'}} color="inherit" />
                            </p>
                        </div>
                    </div>
                    {this.props.product.ProductImage ?
                        this.props.product.ProductImage.length > 0 ?
                            <img style={{width: '165px'},{height: '222px'}} className="img-fluid" src={API.makeFileURL(this.props.product.ProductImage[0].url, null)} alt="" />
                        :null
                    :null
                    }
                </Link>
                <div style={{background: 'inherit', height:'120px'}} className="portfolio-caption">
                    <h4>{this.props.product.name}</h4>
                    <p className="text-muted">{this.props.product.author}</p>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({

})

const mapDispatchToProps = dispatch => ({
    setProductData: (product) => {
        dispatch(SiteActions.setProductData(product));
    }
})
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ShopItems);
