import React,{Component} from 'react';
import Header from '../Common/Header/Header';
import {connect} from 'react-redux';
import {withRouter, Link as RouterLink} from 'react-router-dom';
import Link from '@material-ui/core/Link';
import * as SiteActions from '../../store/actions/siteActions';
import ShopItems from '../../components/Common/ShopItems';

import '../assets/css/shop.css';


import image from '../assets/img/shop-info-bg.jpg';

class Shop extends Component {
    render(){
        return(
            <div>
                <div className="page-empty-banner col-lg-12 col-md-12 col-sm-12">
                    <div className="page-banner page-banner-tm">
                        <Link to="">
                            <img src={image} alt="" />
                        </Link>
                    </div>
                    {/* <div className="col-main">
                        <div className="container">
                            <div className={classes.storeButton}>
                                <Button onClick={this.toggleDrawer} className={classes.buttonSize}>Cua hang</Button>
                            </div>
                        </div>
                    </div> */}
                </div>
                {/* <Drawer
                    classes={{
                        paper: clsx(classes.drawerPaper, !this.state.open && classes.drawerPaperClose)
                    }}
                    variant="permanent"
                    open={this.props.open}
                >
                    <Divider />
                </Drawer> */}
                <div>
                    <section className="bg-light" id="portfolio">
                        <div className="container">
                            
                            <div className="row">
                                
                                {this.props.site.products ?
                                    this.props.site.products.length > 0 ?
                                        this.props.site.products.map((product, i) => {
                                            return (
                                                <ShopItems
                                                    product={product}
                                                    key={i} 
                                                />
                                            )
                                        })
                                    :null
                                :null}
                            </div>
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="text-center">
                                        {this.props.site.productCount > this.props.site.products.length ?
                                                <button className="btn btn-default" onClick={e => {
                                                    this.props.getProducts(this.props.site.products.length);
                                                }}>Xem them...</button>
                                            :null}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    site: state.site
})

const mapDispatchToProps = dispatch => ({
    getProducts: (skip) => {
        dispatch(SiteActions.getProducts(skip));
    },
    getProductCount: () => {
        dispatch(SiteActions.getProductCount());
    }
})

export default withRouter(connect(
    mapStateToProps,
    mapDispatchToProps
)(Shop));
