import React, {Component} from 'react';
import {withFormik} from 'formik';
import * as Yup from 'yup';
import Field from '../Common/Field';

const fields= [
    {name: 'email',type: 'email', placeholder: 'Email address'},
    {name: 'password',type: 'password', placeholder: 'Password'}
];

class Login extends Component {
    constructor(props){
        super(props);
        this.state ={
            email: '',
            password: ''        
        }
    }
    render(){
        return(
        <div className="login-account section-padding">
           <div className="container">
                <div className="row">
                    
                    <div className="col-md-6 col-sm-6">
                       <form action="/" className="create-account-form" method="post">
                            <h2 className="heading-title">
                                LOGIN
                            </h2>
                            {fields.map((field, i) => {
                                return <Field   
                                            {...field}
                                            key={i} 
                                            value={this.state[field.name]}
                                            onChange={e => this.setState({
                                                [field.name]: e.target.value
                                            })}    
                                        />
                            })}
                            <p className="lost-password form-group">
                                <a href="#" rel="nofollow">Forgot your password?</a>
                            </p> 
                            <div className="submit">					
                                <button name="submitcreate" id="submitcreate" type="submit" className="btn-default">
                                    <span>
                                        <i className="fa fa-user left"></i>
                                        SIGN IN
                                    </span>
                                </button>
                            </div>                          
                       </form>
                    </div>
                    <div className="col-md-6 col-sm-6">
                        <form action="#" className="create-account-form" method="post">
                            <h2 className="heading-title">
                                CREATE AN ACCOUNT
                            </h2>
                            <div className="submit">					
                                <button name="submitcreate" id="submitcreate" type="submit" className="btn-default">
                                    <span>
                                        <i className="fa fa-user left"></i>
                                        Create an account
                                    </span>
                                </button>
                            </div>
                        </form>
                    </div>
                </div>               
           </div>
        </div>
        )
    }
}

export default withFormik({
    mapPropstoValues: () => ({
        email: '',
        password: '',
    }),
    validationSchema: Yup.object().shape({
        email: Yup.string().min(3, '').required('Email cua ban :v'),
        password: Yup.string().min(3, '').required("Required")
    }),

    handleSubmit: (values, {setSubmitting}) => {
        alert("login", JSON.stringify(values));
    }
})(Login);
