import React,{Component} from 'react';
import {Link} from 'react-router-dom';

import {withStyles} from '@material-ui/core/styles';

import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import IconButton from '@material-ui/core/IconButton';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ShopSideBarCate from './ShopSideBarCate';


const sidebars = [
    {catename: 'Sach trong nuoc', cateicon: InboxIcon},
]

const styles = theme => ({
    largerText: {
        fontSize: '2rem',
    }
});

class ShopSideBar extends Component {
    render(){
        const classes= this.props;
        return(
            <div style={{width: '250', fontSize: '2rem'}} role= "presentation" onClick={this.toggleDrawer} onKeyDown={this.toggleDrawer}>
                        <List>
                            <Link to="/">
                            <ListItem button default>
                                <ListItemIcon>
                                    <InboxIcon fontSize="large" />
                                </ListItemIcon>
                                <ListItemText disableTypography primary="Tat ca san pham"/>
                            </ListItem>
                            </Link>
                            
                        </List>
                        <Divider />
                        <List>
                            {sidebars.map((sidebar, i) => {
                                return <ShopSideBarCate {...sidebar} key={i} />
                            })}
                        </List>
                    </div>
        )
    }
}
export default (withStyles(styles)(ShopSideBar));
