import React, {Component} from 'react';
import SingleTestimonial from './SingleTestimonial';

import img1 from '../assets/img/testimonial/1.jpg';
import img2 from '../assets/img/testimonial/1.jpg';

const testimonials=[
    {img:img1,author:'Long',authortitle:'dep trai',post:'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation. '},
    {img:img2,author:'gnol',authortitle:'hoc gioi',post:'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation. '},
];

class Testimonial extends Component {
    render(){
        return(
            <div className="testimonial-area text-center">
                <div className="container">
                    <div className="testimonial-title">
                        <h2>OUR TESTIMONIAL</h2>
                        <p>What our clients say about the books reviews and comments</p>
                    </div>
                    <div className="row">
                        <div className="col-md-12">
                            <div className="testimonial-list owl-carousel owl-theme">
                                <div className="owl-wrapper-outer">
                                <div className="owl-wrapper">
                                    {testimonials.map((testimonial,i) =>{
                                        return <SingleTestimonial {...testimonial} key={i} />
                                    })}
                                <div className="owl-controls clickable">
                                    <div className="owl-buttons">
                                        <div className="owl-prev">
                                            <i className="flaticon-arrows"></i>
                                        </div>
                                        <div className="owl-next">
                                            <i className="flaticon-arrows-1"></i>
                                        </div>
                                    </div>
                                </div>
                                </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default Testimonial;
