import React, {Component} from 'react';
import {Link} from 'react-router-dom';

import '../../assets/css/header.css';
import '../../assets/css/page.css';

import BlockContent from './DanhMucSP/BlockContent';
import SlideShow from './SlideShow';

import img1 from '../../assets/img/home/1.png';
import img2 from '../../assets/img/home/2.png';
import img3 from '../../assets/img/home/3.png';
import img4 from '../../assets/img/home/4.png';
import img5 from '../../assets/img/home/5.png';

import topBanner from '../../assets/img/top-banner-2.jpg';

const contents = [
    {name:'sach trong nuoc'},
    {name:'foreign books'},
    {name:'VPP - Dung cu hoc sinh'},
    {name:'Do choi'},
    {name:'tuyen tap'},
    {name:'sach theo nha cung cap'},
    {name:'vpp - dchs theo thuong hieu'},
    {name:'do choi theo thuong hieu'},
    {name:'bach hoa online - luu niem'},
    {name:'hoa qua'},
    {name:'dien nuoc'}
];

const collections = [
    {src: img1,caption: ""},
    {src: img2,caption: ""},
    {src: img3,caption: ""},
    {src: img4,caption: ""},
    {src: img5,caption: ""}
];

class Header extends Component {
    render(){
        return(
            <section id="header" className="header">
                
                <div className="header-breadcrumbs background-menu-homepage">
                    <div className="container">
                        <div className="row custom-menu-homepage">
                            <div className="col-md-3 hidden-max-width-992">
                                <div id="ves-verticalmenu" className="block block-success ves-verticalmenu highlighted hidden-sm offcanvas-actived">
                                    <div className="block-title hidden-xs hidden-sm">
                                        <span>Danh muc san pham</span>
                                        <em className="shapes right"></em>
                                        <em className="line"></em>
                                    </div>
                                    <div className="block-content">
                                        <div className="navbar navbar-inverse">
                                            <div id="verticalmenu" className="verticalmenu offset-canvas-menu-play" role="navigation" data-offcanvas="true">
                                                <div className="navbar">
                                                    <Link to="javascript:;" data-target=".navbar-collapse" data-toggle="collapse" className="navbar-toggle">
                                                        <span className="icon-bar"></span>
                                                        <span className="icon-bar"></span>
                                                        <span className="icon-bar"></span>
                                                    </Link>
                                                    <div className="collapse navbar-collapse navbar-ex1-collapse">
                                                        <ul className="nav navbar-nav vertical menu">
                                                            {contents.map((content, i) => {
                                                                return <BlockContent {...content} key={i} />
                                                            })}
                                                        </ul>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-9">
                                <div className="container">
                                    <div className="container-inner">
                                        <div className="row">
                                            <div className="col-sms12 col-md-9 col-sm-12">
                                                <div className="inivoslider swiper-container swiper-container-horizontal">
                                                    <div className="slider-header">
                                                        <SlideShow 
                                                            input={collections}
                                                            ratio={`3:2`}
                                                            mode={`automatic`}
                                                            timeout={`3000`}
                                                        />
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            
        );
    }
}



export default Header;
