import React, {Component} from 'react';
import {Link} from 'react-router-dom';

class Header extends Component {
    render(){
        return(
            <div className="slider-area">
                <div className="bend niceties preview-1">
                    <div id="ensign-nivoslider" className="slides nivoSlider">
                        <img className="nivo-main-image" src={this.props.image} />
                        <div className="nivo-caption">
                            <div className="text-center" >
                                <div className="layer-1">
                                    <h2 className="title-1">{this.props.title}</h2>
                                </div>
                                <div className="layer-2">
                                    <p className="title-2">{this.props.caption}</p>
                                </div>
                                <div className="layer-3">{
                                    this.props.showButton &&
                                    <Link to="/" className="title-3">{this.props.buttontext}</Link>
                                }
                                </div>
                                <div className="layer-4">
                                    <form action="/" className="title-4">
                                        <input type="text" placeholder={this.props.placeholder} />
                                        <button type="submit"><i className="fa fa-search"></i></button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Header;
