import React, {Component} from 'react';
import SingleInfo from './SingleInfo';

const infos=[
    {title:'Vận chuyển miễn phí', caption:'Trong bán kính 1km quanh cơ sở', icon:'transport', buttonText:'xem thêm'},
    {title:'Liên lạc trực tiếp', caption:'Liên lạc qua Facebook', icon:'social', buttonText:'chat ngay'},
];

class Info extends Component{
    render(){
        return(
            <div className="shop-info-area">
                <div className="container">
                    <div className="row">
                        <div className="banner-list">
                        {infos.map((info, i) => {
                            return <SingleInfo {...info} key={i} />
                        })}
                        </div>
                        <div className="col-md-4 hidden-sm">
                            <div className="single-shop-info">
                                <div className="shop-info-icon">
                                    <i className="flaticon-school"></i>
                                </div>
                                <div className="shop-info-content">
                                    <h2>Đặt trước</h2>
                                    <p>quý khách order trước hàng hóa hiện chưa có trong nhà sách', icon:'', buttonText:'</p>
                                    <Link to="/">Order now</Link>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div> 
        );
    }
}

export default Info;
