const defaultState = {
    posts: [],
    postCount: 0,
    post: {},
    products: [],
    product: {},
    productCount: 0,
    addedProducts: [],
    total: 0
}

const site = (state = defaultState, action) => {
    switch(action.type){
        case 'GOT_POST_COUNT':
            return {
                ...state,
                postCount: action.payload
            }
        case 'GOT_SITE_POSTS':
            return {
                ...state,
                posts: action.skip ? state.posts.concat(action.payload) : action.payload
            }
        case 'SET_DEFAULT_POST_DATA':
            return {
                ...state,
                post: action.payload
            }
        case 'SET_FULL_POST_DATA': {
            return {
                ...state,
                post: {
                    ...state.post,
                    ...action.payload
                }
            }
        }
        case 'GOT_PRODUCT_COUNT':
            return {
                ...state,
                productCount: action.payload
            }
        case 'GOT_SITE_PRODUCTS':
            return {
                ...state,
                products: action.skip ? state.products.concat(action.payload) : action.payload
            }
        case 'GOT_HOME_PRODUCT_COUNT':
            return {
                ...state,
                productCount: action.payload
            }
        case 'GOT_HOME_PRODUCTS':
            return {
                ...state,
                products: action.skip ? state.products.concat(action.payload) : action.payload
            }
        case 'SET_DEFAULT_PRODUCT_DATA':
            return {
                ...state,
                product: action.payload
            }
        case 'SET_FULL_PRODUCT_DATA': {
            return {
                ...state,
                product: {
                    ...state.product,
                    ...action.payload
                }
            }
        }
        case 'ADDED_COMMENT': {
            return {
                ...state,
                product: {
                    ...state.product,
                    Comment: state.product.Comment.concat(action.payload)
                }
            }
        }
        case 'ADD_TO_CART':{
            let addedProduct = action.payload
            {
                
                let newToTal = state.total + addedProduct.newPrice

                return{
                    ...state,
                    addedProducts: state.addedProducts.length > 0 ? state.addedProducts.concat(action.payload) : action.payload,
                    total: newToTal
                }
            }
        }

        default:
            return state
    }
}

export default site;
