import React, {Component} from 'react';
import {Link} from 'react-router-dom';

import classNames from 'clsx';
import {fade, withStyles} from '@material-ui/core/styles';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';

//Drawer imports
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import IconButton from '@material-ui/core/IconButton';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import Divider from '@material-ui/core/Divider';

import InputBase from '@material-ui/core/InputBase';
import Badge from '@material-ui/core/Badge';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import ShoppingCart from '@material-ui/icons/ShoppingCart';
import MoreIcon from '@material-ui/icons/MoreVert';

import MailIcon from '@material-ui/icons/Mail';
import NotificationsIcon from '@material-ui/icons/Notifications';

import img1 from './assets/img/logotm.jpg';
import Sidebar from './Common/SideBar';
import Shop from './Pages/Shop';

const drawerWidth = 0;

const styles = theme => ({
    root: {
        display: 'block',
    },
    toolbar: {
        paddingRight: 24,
    },
    appBar: {
        backgroundColor: 'white',
        zIndex: theme.zIndex.drawer + 1,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen
        }),
    },
    appBarShift: {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen
        }),
    },
    appBarSpace: theme.mixins.toolbar,
    drawerPaper: {
        position: 'relative',
        whiteSpace: 'noWrap',
        width: drawerWidth,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen
        }),
    },
    drawerPaperClose: {
        overflowX: 'hidden',
        width: theme.spacing.unit * 7,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen
        }),
    },
    toolbarIcon: {
        color: 'black',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: '0 8px',
        ...theme.mixins.toolbar
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing.units * 3,
        height: '100vh',
        overflow: 'auto'
    },
    grow: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
        color: 'black',
    },
    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    search: {

        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.black, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.black, 0.25),
        },
        marginRight: theme.spacing(2),
        marginLeft: 0,
        width: '80%',
        height: '40px',
        marginLeft: theme.spacing(2),
        [theme.breakpoints.up('md')]: {
            marginLeft: theme.spacing(15),
            marginRight: theme.spacing(15),
            width: '60%',
        },
    },
    searchIcon: {
        color: 'black',
        padding: theme.spacing(0, 2),
        height: '100%',
        width: '30px',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
        height: '40px',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
        transition: theme.transitions.create('width'),
        width: '80ch',
        height:'40px',

        [theme.breakpoints.up('md')]: {
          width: '100ch',
        },
    },
    sectionDesktop: {
        display: 'none',
        [theme.breakpoints.up('md')]: {
          display: 'flex',
        },
    },
    sectionMobile: {
        display: 'flex',
        [theme.breakpoints.up('md')]: {
          display: 'none',
        },
    },
    logoIcon: {
        width: '80px',
        height: '50px',
    },
    mobileMenu: {
        position: 'relative',
        whiteSpace: 'noWrap',
        width: drawerWidth,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen
        }),
    },
})

class PageWrapper extends Component {
    constructor(props){
        super(props);

        this.state = {
            open: false
        }
    }
    
    handleMobileMenuOpen = (e) => {
        this.setState({open: true});
    }

    handleMobileMenuClose = (e) => {
        this.setState({open: false});
    }


    render(){
        const {classes} = this.props;
        
        const menuId = 'primary-search-account-menu';

        
        const mobileMenuId = 'primary-search-account-menu-mobile';
        
        return(
		<div className={classNames(classes.root, classes.grow)}>
            <AppBar className={classNames(classes.appBar, this.state.open && classes.appBarShift)}>
                <Toolbar className={classes.toolbar}>
                    
                    <Typography className={classes.title}
                        component="h1"
                        variant="h6"
                        color="inherit"
                        noWrap
                    ><Link to="/"><img className={classes.logoIcon} src={img1} /></Link></Typography>
                    <div className={classes.search}>
                        <div className={classes.searchIcon}>
                            <SearchIcon 
                                fontSize="large"
                            />
                        </div>
                        <InputBase
                            placeholder="Search..."
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search'}}
                        />
                    </div>
                    <div className={classes.grow} />
                    <div className={classes.sectionDesktop}>
                        <IconButton arial-label="" color="inherit">
                            <Badge badgeContent={1} color="secondary">
                                <ShoppingCart on color ="primary" />
                            </Badge>
                        </IconButton>
                        <IconButton
                            edge="end"
                            aria-label="account of current user"
                            aria-controls=""
                            aria-haspopup="true"
                            onClick=""
                            color="inherit"
                            >
                            <AccountCircle color="primary" />
                        </IconButton>            
                    </div>
                    <div className={classes.sectionMobile}>
                        <IconButton
                        aria-label="show more"
                        aria-controls={mobileMenuId}
                        aria-haspopup="true"
                        onClick={this.handleMobileMenuOpen}
                        color="black"
                        >
                        <MoreIcon />
                        </IconButton>
                    </div>
                </Toolbar>
                
            </AppBar>
            <Menu
                classes={{
                    paper: classes.mobileMenu
                }}
                anchorOrigin={{vertical: 'top', horizontal: 'right'}}
                keepMounted
                transformOrigin={{vertical: 'top', horizontal:'right'}}
                open={this.state.open}
                variant="permanent"
                onClose={this.handleMobileMenuClose}
            >
                <MenuItem>
                    <IconButton color="inherit">
                        <Badge badgeContent={1} color="secondary">
                            <ShoppingCart />
                        </Badge>
                    </IconButton>
                    <p>Gio hang`</p>
                </MenuItem>
                <MenuItem>
                    <IconButton color="inherit">
                        <AccountCircle />
                    </IconButton>
                    <p>Profile</p>
                </MenuItem>

            </Menu>
            
            
            <main className={classes.content}>
                <div className={classes.appBarSpace} />
                {this.props.children}
            </main>
            </div>
        )
    }
}

export default withStyles(styles)(PageWrapper);
