import React,{Component} from 'react';
import {Link} from 'react-router-dom';

import {withStyles} from '@material-ui/core/styles';

import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import IconButton from '@material-ui/core/IconButton';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';


const styles = theme => ({
    largerText: {
        fontSize: '2rem',
    }
});

class ShopSideBar extends Component {
    render(){
        const classes= this.props;
        return(
            <div style={{width: '250'}} role= "presentation" onClick={this.toggleDrawer} onKeyDown={this.toggleDrawer}>
                        <List>
                            <Link to="/">
                            <ListItem button default>
                                
                                <ListItemText style={{fontSize: '2rem'}} disableTypography primary="Tat ca san pham"/>
                            </ListItem>
                            </Link>
                            
                        </List>
                        <Divider />
                        <List>
                            <ListItem button >
                                <ListItemIcon>
                                    <InboxIcon />
                                </ListItemIcon>
                                <ListItemText />
                            </ListItem>
                            <ListItem button >
                                <ListItemIcon>
                                    <MailIcon />
                                </ListItemIcon>
                                <ListItemText />
                            </ListItem>
                        </List>
                    </div>
        )
    }
}
export default (withStyles(styles)(ShopSideBar));
