import React, {Component} from 'react';
import {Link as RouterLink} from 'react-router-dom';
import Link from '@material-ui/core/Link';
import {connect} from 'react-redux';
import * as SiteActions from '../store/actions/siteActions';


import classNames from 'clsx';
import {fade, withStyles} from '@material-ui/core/styles';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';

//Drawer imports
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import IconButton from '@material-ui/core/IconButton';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import Divider from '@material-ui/core/Divider';

import InputBase from '@material-ui/core/InputBase';
import Badge from '@material-ui/core/Badge';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import ShoppingCart from '@material-ui/icons/ShoppingCart';
import MoreIcon from '@material-ui/icons/MoreVert';

import MailIcon from '@material-ui/icons/Mail';
import NotificationsIcon from '@material-ui/icons/Notifications';

import img1 from './assets/img/logotm.png';
import img2 from './assets/img/logotext1.png';

import Sidebar from './Common/SideBar';
import Shop from './Pages/Shop';
import Cart from './Common/Cart';

const drawerWidth = 0;
const logoWidth = 80;

const styles = theme => ({
    root: {
        display: 'block',
    },
    toolbar: {
    },
    appBar: {
        backgroundColor: 'white',
        zIndex: theme.zIndex.drawer + 1,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen
        }),
    },
    appBarShift: {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen
        }),
    },
    appBarSpace: theme.mixins.toolbar,
    drawerPaper: {
        position: 'relative',
        whiteSpace: 'noWrap',
        width: drawerWidth,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen
        }),
    },
    drawerPaperClose: {
        overflowX: 'hidden',
        width: theme.spacing(7),
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen
        }),
    },
    toolbarIcon: {
        color: 'black',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: '0 8px',
        ...theme.mixins.toolbar
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
        height: '100vh',
        overflow: 'auto',
        [theme.breakpoints.down('md')]: {
            padding: 0,
        }
    },
    grow: {
        flexGrow: 1,
        textAlign: 'center',
        display: 'flex'
    },
    menuButton: {
        marginRight: theme.spacing(2),
        color: 'black',
    },
    title: {
        width: '30%',
        display: 'block',
        [theme.breakpoints.up('lg')]: {
            width: '16%',
            
        },
    },
    logoIcon: {
        position: 'relative',
        width: '80px',
        height: '50px',
        
    },
    titleText: {
        width: `calc(100% - ${logoWidth}px)`,
        height: '50px',
        
        [theme.breakpoints.down('xs')]: {
            display: 'none',
        },
    },
    search: {

        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.black, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.black, 0.25),
        },
        marginRight: theme.spacing(2),
        width: '40%',
        height: '40px',
        [theme.breakpoints.up('lg')]: {
            marginRight: theme.spacing(2),
            width: '50%',
        },
    },
    searchIcon: {
        color: 'black',
        padding: theme.spacing(0, 2),
        height: '100%',
        width: '30px',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
        width: '100%',
        height: '40px',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
        transition: theme.transitions.create('width'),
        width: '100%',
        height:'40px',
    },
    buttonStore: {
        borderRight: '1px solid',
        width: '40%',
        fontSize: 'large',
        fontFamily: 'monospace',
        color: '#d6993a',
        [theme.breakpoints.down('xs')]: {
            fontSize: 'medium',
        },
        [theme.breakpoints.up('md')]: {
            fontSize: 'x-large',
        },
        [theme.breakpoints.up('lg')]: {
            fontSize: 'xx-large',
        },
    },
    buttonContact: {
        width: '50%',
        fontSize: 'large',
        fontFamily: 'monospace',
        color: '#d6993a',
        [theme.breakpoints.down('xs')]: {
            fontSize: 'medium',
        },
        [theme.breakpoints.up('md')]: {
            fontSize: 'x-large',
        },
        [theme.breakpoints.up('lg')]: {
            fontSize: 'xx-large',
        },
    },
    sectionDesktop: {
        display: 'none',
        [theme.breakpoints.up('md')]: {
          display: 'flex',
        },
    },
    sectionMobile: {
        display: 'flex',
        [theme.breakpoints.up('md')]: {
          display: 'none',
        },
    },
    mobileMenu: {
    },
    shopDrawer: {
        width: '30%'
    }
});

class PageWrapper extends Component {
    constructor(props){
        super(props);

        this.state = {
            open: false,
            anchor: null
        }
    }
    
    toggleDrawer = () => {
        if(this.state.open){
            this.setState({open: false})
        }else{
            this.setState({open: true})
        }
    }

    handleClick = (event) => {
        this.setState({anchor: event.currentTarget})
    }

    handleClose = () => {
        this.setState({anchor: null})
    }


    render(){
        const {classes} = this.props;
        

        
        const mobileMenuId = 'primary-search-account-menu-mobile';
        
        return(
		<div className={classNames(classes.root, classes.grow)}>
            <AppBar  className={classes.appBar}>
                <Toolbar className={classes.toolbar}>
                    
                    <Typography className={classes.title}
                        component="h1"
                        variant="h6"
                        color="inherit"
                        noWrap
                    >
                        <Link href="/">
                            <img className={classes.logoIcon} src={img1} />
                            <img className={classes.titleText} src={img2} />
                            
                        </Link>
                    </Typography>
                    <div className={classes.search}>
                        <div className={classes.searchIcon}>
                            <SearchIcon 
                                fontSize="large"
                            />
                        </div>
                        <InputBase
                            placeholder="Search..."
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search'}}
                        />
                    </div>
                    <div className={classes.grow}>
                        <div className={classes.buttonStore}>
                            <Link href='/shop' color='inherit'>
                                Cửa hàng
                            </Link>
                        </div>
                        <div className={classes.buttonContact}>
                            <Link href='/contact' color='inherit'>
                                Liên hệ
                            </Link>
                        </div>
                    </div>
                    <div className={classes.sectionDesktop}>
                        <IconButton arial-label="" color="inherit">
                            <Badge badgeContent={this.props.site.addedProducts.length} color="secondary" >
                                    <ShoppingCart fontSize="large" onClick={this.toggleDrawer} color ="primary" />
                                
                            </Badge>
                        </IconButton>
                        <IconButton
                            edge="end"
                            aria-label="account of current user"
                            aria-controls=""
                            aria-haspopup="true"
                            color="inherit"
                            >
                            <Link href="/admin">
                                <AccountCircle fontSize="large" color="primary" />
                            </Link>
                        </IconButton>            
                    </div>
                    <div className={classes.sectionMobile}>
                        <IconButton
                        aria-label="show more"
                        aria-controls={mobileMenuId}
                        aria-haspopup="true"
                        onClick={this.handleClick}
                        >
                        <MoreIcon />
                        </IconButton>
                    </div>
                </Toolbar>
                
            </AppBar>
            <Menu
                className={classes.mobileMenu}
                anchorEl={this.state.anchor}
                anchorOrigin={{vertical: 'top', horizontal: 'right'}}
                keepMounted
                id={mobileMenuId}
                transformOrigin={{vertical: 'top', horizontal:'right'}}
                open={Boolean(this.state.anchor)}
                onClose={this.handleClose}
            >
                <MenuItem onClick={this.toggleDrawer}>
                    <IconButton color="inherit">
                        <Badge 
                        badgeContent={1} 
                        color="secondary">
                            <ShoppingCart fontSize="large" />
                        </Badge>
                    </IconButton>
                    <p>Gio hang`</p>
                </MenuItem>
                <MenuItem>
                    <IconButton component={RouterLink} to="/admin" color="inherit">
                        <AccountCircle fontSize="large" />
                    </IconButton>
                    <p>Profile</p>
                </MenuItem>
            </Menu>
            <Drawer
                classes={{
                    paper: classes.shopDrawer
                }}
                anchor="right"
                open={this.state.open} 
                onClose={this.toggleDrawer}
            >
                
                <Divider />
                <Cart />
            </Drawer>
            
            <main className={classes.content}>
                <div className={classes.appBarSpace} />
                {this.props.children}
            </main>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    site: state.site
})

export default connect(
    mapStateToProps
)(withStyles(styles)(PageWrapper));
