                                                        <SlideShow 
                                                            input={collections}
                                                            ratio={`3:2`}
                                                            mode={`automatic`}
                                                            timeout={`3000`}
                                                        />
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            
        );
    }
}



export default Header;
