import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import API from '../../utils/api';
import Badge from '@material-ui/core/Badge';
import ShoppingCart from '@material-ui/icons/ShoppingCart';
import * as SiteActions from '../../store/actions/siteActions';

import '../assets/css/shop.css';

import delIcon from '@material-ui/icons/Clear';

class Cart extends Component {
    render(){
        let addedProducts = this.props.products.length ?
        (
            this.props.products.map(product => {
                return(
                       <div className="shelf-item_tm" key={product.id}>
                        <delIcon className="sgelf-item_del_tm" />
                       <div className="shelf-item_thumb_tm">
                           <img alt={product.name} src={API.makeFileURL(product.ProductImage[0].url, null)} />
                       </div>
                       <div className="shelf-item_details_tm">
                           <p className="title_tm">{product.name}</p>
                           <p className="desc_tm">
                                {product.publishing}
                               <br />
                               Số lượng: {product.quantity}
                           </p>
                       </div>
                       <div className="shelf-item_price_tm">
                           <p>{product.newPrice} </p>
                       </div>
                   </div>                  
                )
            })
        ):
        (
            <p>Nothing</p>    
        )
        return(
            <div className="float-cart_content_tm">
                <div className="float-cart_header_tm">
                    <span>
                    <Badge className="bag_tm" badgeContent={addedProducts.length} color="secondary" >
                        <ShoppingCart fontSize="large" onClick={this.toggleDrawer} color ="primary" /> 
                    </Badge>
                    </span>
                    <span className="header-title_tm">Giỏ Hàng</span>
                </div>
                <div className="float-cart__shelf-container_tm">
                    {addedProducts}
                </div>
                <div className="float-cart_footer_tm">
                    <div className="sub_tm">Tổng số tiền: </div>
                    <div className="sub-price_tm">
                        <p className="sub-price_val_tm">{this.props.cart.total}</p>
                        <small className="sub-price_installment_tm">
                            <span></span>
                        </small>
                    </div>
                    <div className="buy-btn_tm">Mua ngay</div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        site: state.site
    }
}

const mapDispatchToProps = (dispatch) => {
    return {

    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Cart);
