import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import SingleBanner from './SingleBanner';


import img1 from '../assets/img/banner/1.jpg';
import img2 from '../assets/img/banner/2.jpg';
import img3 from '../assets/img/banner/3.jpg';

const banners= [
    {title:'Mới 2020', price: '99.999', img:img1},
    {title:'Mới 2020', price: '99.999', img:img2},
    {title:'Mới 2020', price: '99.999', img:img3}
    
];


class Banner extends Component{
    render(){
        return(
            <div>
                <div className="online-banner-area">
                    <div className="container">
                        <div className="banner-title text-center">
                            <h2>TUỆ MINH <span>BOOK STORE</span></h2>
                            <p>Địa chỉ: ....................................</p>
                        </div>
                        <div className="row">
                            <div className="banner-list">
                                {banners.map((banner, index) => {
                                    return <SingleBanner {...banner} key={index} />
                                })}
                                 <div className="col-md-4 hidden-sm">
                                    <div className="single-banner">
                                        <Link to="/">
                                            <img src={this.props.img} alt="" />
                                        </Link>
                                        <div className="price">{this.props.price}<span>ᵈ</span></div>
                                        <div className="banner-bottom text-center">
                                            <Link to="/">{this.props.title}</Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Banner;
