import React, {Component} from 'react';
import {Link} from 'react-router-dom';

class Header extends Component {
    render(){
        return(
            <div>
				<div className="slider-area brown__nav slider--15 slide__activation slide__arrow01 owl-carousel owl-theme">
					<div className="slide animation__style10 bg-image--1 fullscreen align__center--left" style={{backgroundImage: `url(${this.props.image})`}}>
						<div className="container">
							<div className="row">
								<div className="col-lg-12">
									<div className="slider__content">
										<div className="contentbox">
                                            <h2>{this.props.title1}<span>{this.props.title2}</span></h2>
                                            {this.props.showButton &&
											    <Link className="shopbtn" to={this.props.linkto}>{this.props.linktitle}</Link>
                                            }
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>					
				</div>
			</div>
        );
    }
}

export default Header;
