import React,{Component} from 'react';
import Header from '../Common/Header/Header';
import {Link} from 'react-router-dom';

import '../assets/css/shop.css';

import clsx from 'clsx';
import {withStyles} from '@material-ui/core/styles';


import image from '../assets/img/shop-info-bg.jpg';
import { Button, Drawer } from '@material-ui/core';

const drawerWidth= 240;

const styles = theme => ({
    storeButton: {
        width: '250px',
        margin: '10px auto',
        display: 'block',
        fontSize: '14px',
        marginBottom: '10px',
        color: '#fff',
        fontWeight: 'bold',
        textAlign: 'center',
        padding: '10px',
        backgroundColor: '#ff9800',
        borderRadius: '25px'
    },
    buttonSize: {
        fontSize: 'large',
        padding: '0',
        width: '100%'
    },
    drawerPaper: {
        position: 'relative',
        whiteSpace: 'noWrap',
        width: drawerWidth,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen
        }),
    },
    drawerPaperClose: {
        overflowX: 'hidden',
        width: theme.spacing.unit * 7,
        transition: theme.transitions.create('width', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen
        }),
    },
    toolbarIcon: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: '0 8px',
        ...theme.mixins.toolbar
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing.units * 3,
        height: '100vh',
        overflow: 'auto'
    },
    list: {
        width: 250,
    },
        fullList: {
        width: 'auto',
    },
})

class Shop extends Component {
    constructor(props){
        super(props);

        this.state = {
            open: true
        }
    }

    handleDrawerOpen = (e) => {
        this.setState({open: true});
    }

    handleDrawerClose = (e) => {
        this.setState({open: false});
    }

    render(){
        const {classes} = this.props;

        return(
            <div>
                <div className="page-empty-banner col-lg-12 col-md-12 col-sm-12">
                    <div className="page-banner page-banner-tm">
                        <Link to="">
                            <img src={image} alt="" />
                        </Link>
                    </div>
                    <div className="col-main">
                        <div className="container">
                            <div className={classes.storeButton}>
                                <Button onClick={this.handleDrawerOpen} className={classes.buttonSize}>Cua hang</Button>
                            </div>
                        </div>
                    </div>
                </div>
                <Drawer
                    classes={{
                        paper: clsx(classes.drawerPaper, !this.state.open && classes.drawerPaperClose)
                    }}
                    variant="permanent"
                    open={true}
                >
                    <div
      className={clsx(classes.list, {
        [classes.fullList]: anchor === 'top' || anchor === 'bottom',
      })}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <List>
        {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {['All mail', 'Trash', 'Spam'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </div>
                </Drawer>
            </div>
        )
    }
}

export default withStyles(styles)(Shop);
