import React,{Component} from 'react';
import Header from '../Common/Header/Header';
import {Link} from 'react-router-dom';

import '../assets/css/shop.css';

import classNames from 'clsx';
import {withStyles} from '@material-ui/core/styles';


import image from '../assets/img/shop-info-bg.jpg';
import { Button } from '@material-ui/core';

const styles = theme => ({
    storeButton: {
        width: '250px',
        margin: '10px auto',
        display: 'block',
        fontSize: '14px',
        marginBottom: '10px',
        color: '#fff',
        fontWeight: 'bold',
        textAlign: 'center',
        padding: '10px',
        backgroundColor: '#ff9800',
        borderRadius: '25px'
    },
    buttonSize: {
        fontSize: 'large',
        padding: '0',
        width: '100%'
    }
})

class Shop extends Component {
    render(){
        const {classes} = this.props;

        return(
            <div>
                <div className="page-empty-banner col-lg-12 col-md-12 col-sm-12">
                    <div className="page-banner page-banner-tm">
                        <Link to="">
                            <img src={image} alt="" />
                        </Link>
                    </div>
                    <div className="col-main">
                        <div className="container">
                            <div className={classes.storeButton}>
                                <Button className={classes.buttonSize}>All</Button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default withStyles(styles)(Shop);
