import React, {Component} from 'react';
import {Link} from 'react-router-dom';


class SingleBlog extends Component {
    render(){
        return(
            <div className="owl-item blog-area-item">
                <div className="col-md-3">
                    <div className="single-blog">
                        <Link to="single-#">
                            <img src={this.props.img} alt="" />
                        </Link>
                        <div className="blog-info text-center">
                            <Link to="/"><h2>{this.props.title}</h2></Link>
                            <div className="blog-info-bottom">
                                <span className="blog-author">BY: <Link to="/">{this.props.source}</Link></span>
                                <span className="blog-date">{this.props.date}</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default SingleBlog;
