import React, {Component} from 'react';
import {connect} from 'react-redux';
import {withRouter, Link, Link as RouterLink} from 'react-router-dom';
import ShopItems from '../../Common/ShopItems';

import * as SiteActions from '../../../store/actions/siteActions';


import '../../assets/css/blog.css';
import '../../assets/css/shop.css';



class AllProducts extends Component {

    componentDidMount(){
        this.props.getSiteProducts(0);
        this.props.getProductCount();
    }

    render(){
        return(
            <div>
                <div className="container">
                    <div className="row">
                        {this.props.site.products ?
                            this.props.site.products.length > 0 ?
                                this.props.site.products.map((product, i) => {
                                    return (
                                        <ShopItems
                                            product={product}
                                            key={i} 
                                        />
                                    )
                                })
                            :null
                        :null}
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-12">
                        <div className="text-center">
                            {this.props.site.productCount > this.props.site.products.length ?
                                <div className="tabs-xem-them xem-them-item-aaa">
                                    <Link onClick={e => {
                                        this.props.getSiteProducts(this.props.site.products.length);
                                    }}>Xem ThÃªm</Link>
                                </div>
                            :null}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    site: state.site
})

const mapDispatchToProps = dispatch => ({
    getSiteProducts: (skip) => {
        dispatch(SiteActions.getProducts(skip));
    },
    getProductCount: () => {
        dispatch(SiteActions.getProductCount());
    }
})

export default withRouter(connect(
    mapStateToProps,
    mapDispatchToProps
)(AllProducts));
