import React, {Component} from 'react';
import {connect} from 'react-redux';
import {withRouter, Link, Link as RouterLink} from 'react-router-dom';
import * as SiteActions from '../../../store/actions/siteActions';
import ShopItems from '../ShopItems';

import '../../assets/css/blog.css'

class HomeShop extends Component {

    componentDidMount(){
        this.props.getHomeProducts(0, this.props.cate);
        this.props.getHomeProductCount();
    }

    render(){
        return(
            <div id="categorytab-caphs" className="categorytab-slider fhs-grid">
                <div>
                    <div className="tabslider-header">
                        <div className="tabslider-title girdslider-title-aaa">
                            {this.props.cate}
                        </div>
                    </div>
                    <div className="tab_container">
                        <div className="col-lg-12 col-md-12 col-sm-12 fhs-full-p">    
                            <div id="tabcaphs-caphs" className="tab_content_caphs" > 
                                <ul className="bxslider">
                                    <div className="row">
                                        {this.props.site.products.map((product, i) => {
                                            return(
                                                <ShopItems 
                                                    product={product}
                                                    key={i}
                                                />
                                            )
                                        })}
                                    </div>
                                </ul>
                                {this.props.site.products ?
                                    this.props.site.productCount > this.props.site.length ?                        
                                        <div className="tabs-xem-them xem-them-item-aaa">
                                            <Link onClick={e => {
                                                this.props.getHomeProducts(this.props.site.products.length);
                                            }}>Xem ThÃªm</Link>
                                        </div>
                                    :null
                                :null}    
                            </div>
                        </div>
                    </div>
                </div> 
            </div>
        )
    }
}
const mapStateToProps = state => ({
    site: state.site
})

const mapDispatchToProps = dispatch => ({
    getHomeProducts: (skip, cate) => {
        dispatch(SiteActions.getHomeProducts(skip, cate));
    },
    getHomeProductCount: () => {
        dispatch(SiteActions.getHomeProductCount());
    }
})

export default withRouter(connect(
    mapStateToProps,
    mapDispatchToProps
)(HomeShop));
